import React, { useState, useContext } from 'react';
import { AuthContext } from './AuthContext'; // Import the AuthContext

const PaymentCreation = () => {
    const { auth } = useContext(AuthContext); // Get the auth context
    const [paymentData, setPaymentData] = useState({
        recipientName: '',
        recipientBank: '',
        recipientAccountNumber: '',
        amount: '',
        swiftCode: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setPaymentData({
            ...paymentData,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Add loginID and createdAt to the payment data
        const dataToSend = {
            ...paymentData,
            loginID: auth.username,
            createdAt: new Date()
        };

        try {
            const response = await fetch('/create-payment', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(dataToSend)
            });

            if (response.ok) {
                alert('Payment created successfully!');
            } else {
                alert('Failed to create payment.');
            }
        } catch (error) {
            console.error('Error:', error);
            alert('An error occurred while creating the payment.');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>Recipient Name:</label>
                <input
                    type="text"
                    name="recipientName"
                    value={paymentData.recipientName}
                    onChange={handleChange}
                    required
                />
            </div>
            <div>
                <label>Recipient Bank:</label>
                <input
                    type="text"
                    name="recipientBank"
                    value={paymentData.recipientBank}
                    onChange={handleChange}
                    required
                />
            </div>
            <div>
                <label>Recipient Account Number:</label>
                <input
                    type="text"
                    name="recipientAccountNumber"
                    value={paymentData.recipientAccountNumber}
                    onChange={handleChange}
                    required
                />
            </div>
            <div>
                <label>Amount:</label>
                <input
                    type="number"
                    name="amount"
                    value={paymentData.amount}
                    onChange={handleChange}
                    required
                />
            </div>
            <div>
                <label>SWIFT Code:</label>
                <input
                    type="text"
                    name="swiftCode"
                    value={paymentData.swiftCode}
                    onChange={handleChange}
                    required
                />
            </div>
            <button type="submit">Create Payment</button>
        </form>
    );
};

export default PaymentCreation;
