import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from '../AuthContext.js';
import { NavLink, useNavigate } from 'react-router-dom';
import axios from 'axios';
import notificationSound from './notification-sound.mp3'; // Import the sound file
import './Home.css';

const Home = () => {
    const { auth } = useContext(AuthContext);
    const navigate = useNavigate();
    const [lastNotificationCount, setLastNotificationCount] = useState(0); // State to track the last notification count

    const playNotificationSound = () => {
        const audio = new Audio(notificationSound); // Use the imported sound file
        audio.play().then(() => {
            console.log("Notification sound played."); // Debug message
        }).catch(error => {
            console.error("Error playing sound:", error);
        });
    };

    useEffect(() => {
        if (!auth.token) {
            navigate('/'); // Redirect to home if not logged in
        } else {
            const fetchNotifications = async () => {
                try {
                    const response = await axios.get('https://localhost:3001/notify', { // Use http instead of https
                        headers: {
                            Authorization: `Bearer ${auth.token}`,
                        },
                        params: {
                            username: auth.username,
                        },
                    });

                    console.log("Fetched notifications:", response.data); // Log fetched notifications

                    // Check if there are new notifications
                    if (response.data.length > lastNotificationCount) {
                        console.log("New notifications received, playing sound."); // Debug message
                        playNotificationSound(); // Play sound when new notifications are fetched
                    }
                    setLastNotificationCount(response.data.length); // Update the last notification count
                } catch (error) {
                    console.error("Error fetching notifications:", error);
                }
            };

            // Fetch notifications immediately on login
            fetchNotifications();

            const interval = setInterval(fetchNotifications, 5000); // Fetch notifications every 5 seconds
            return () => clearInterval(interval); // Cleanup on unmount
        }
    }, [auth.token, auth.username, navigate, lastNotificationCount]); // Added lastNotificationCount to dependencies

    if (!auth.token) {
        return (
            <div className="dashboard-container">
                <h1>Welcome to <span className="highlight">TransactIO</span></h1>
                <p>Please log in to access your account and make payments.</p>
            </div>
        );
    }

    const banks = ["ABSA", "NEDBANK", "FNB", "CAPITEC", "STANDARD BANK"];
    const randomBank = banks[Math.floor(Math.random() * banks.length)];

    const maskAccountNumber = (accountNumber) => {
        return accountNumber.slice(0, -3).replace(/./g, 'x') + accountNumber.slice(-3);
    };

    const bankingDetails = {
        accountNumber: maskAccountNumber(auth.accountNumber || "123456789"), // Use the account number from auth
        bankName: randomBank,
        balance: "R100,000"
    };

    return (
        <div className="dashboard-container">
            <h1>Welcome to TransactIO</h1>
            <p>Hello, {auth.username}</p>
            <div className="dashboard">
                <h2>Banking Details</h2>
                <p1>Account Number: {bankingDetails.accountNumber}</p1>
                <p1>Bank Name: {bankingDetails.bankName}</p1>
                <p1>Balance: {bankingDetails.balance}</p1>
                <NavLink to="/paymentCreate">
                    <button>Make International Payment</button>
                </NavLink>
            </div>
        </div>
    );
};

export default Home;
