import React, { createContext, useState } from 'react';

export const AuthContext = createContext();

// Create a provider component
export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState({
        token: null,
        username: null,
        accountNumber: null, // Add account number to the auth state
        isEmployee: false // Add isEmployee flag to the auth state
    });

    // Login function to set authentication state
    const login = (token, username, accountNumber, isEmployee) => {
        setAuth({
            token,
            username,
            accountNumber, // Set the account number during login
            isEmployee // Set the isEmployee flag during login
        });
    };

    // Logout function to clear authentication state
    const logout = () => {
        setAuth({
            token: null,
            username: null,
            accountNumber: null, // Clear account number on logout
            isEmployee: false // Clear isEmployee flag on logout
        });
    };

    return (
        <AuthContext.Provider value={{ auth, setAuth, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};
