import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function PostCreate() {
    const [form, setForm] = useState({
        content: '',
        image: null
    });

    const navigate = useNavigate(); // Correct usage of useNavigate

    function updateForm(value) {
        return setForm((prev) => {
            return { ...prev, ...value };
        });
    }

    function handleImageChange(e) {
        setForm({ ...form, image: e.target.files[0] });
    }

    async function onSubmit(e) {
        e.preventDefault();

        // Create a new FormData object to handle file upload
        const formData = new FormData();
        formData.append('content', form.content);
        if (form.image) {
            formData.append('image', form.image);
        }

        // Log the form data to verify it
        for (let pair of formData.entries()) {
            console.log(pair[0] + ': ' + pair[1]);
        }

        try {
            // Retrieve the token from local storage
            const token = localStorage.getItem('token');
            if (!token) {
                throw new Error('No token found. Please log in.');
            }

            // Send a POST request to the server
            const response = await fetch('https://localhost:3001/post/upload', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`
                },
                body: formData
            });

            // Check if the response is ok (status in the range 200-299)
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            // Parse the JSON response
            const data = await response.json();
            console.log('Post created:', data);

            // Reset the form state
            setForm({ content: '', image: null });

            // Navigate to the home page or another page
            navigate('/');
        } catch (error) {
            console.error('Error creating post:', error);
            window.alert(`An error occurred: ${error.message}`);
        }
    }

    return (
        <div className="container">
            <h3>Create New Post</h3>
            <form onSubmit={onSubmit}>
                <div className="form-group">
                    <label htmlFor='content'>Content</label>
                    <input
                        type="text"
                        className='form-control'
                        id="content"
                        value={form.content}
                        onChange={(e) => updateForm({ content: e.target.value })}
                    />
                </div>
                <div className='form-group'>
                    <label htmlFor='image'>Image</label>
                    <input
                        type='file'
                        className='form-control'
                        id='image'
                        accept='image/*'
                        onChange={handleImageChange}
                    />
                </div>
                <div className='form-group'>
                    <input
                        type='submit'
                        value='Create Post'
                        className='btn btn-primary'
                    />
                </div>
            </form>
        </div>
    );
}
