import React, { useContext, useEffect, useState, useCallback } from 'react';
import { AuthContext } from '../AuthContext.js';
import { NavLink, useNavigate } from 'react-router-dom';
import './Home.css';
import loginSound from './notification-sound.mp3'; // Import the sound file
import axios from 'axios';

const Home = () => {
    const { auth } = useContext(AuthContext);
    const navigate = useNavigate();
    const [notifications, setNotifications] = useState([]);
    const [lastNotificationCount, setLastNotificationCount] = useState(0); // Track last notification count

    // Function to play notification sound
    const playNotificationSound = () => {
        const audio = new Audio(loginSound); // Use the imported sound file
        audio.play();
    };

    // Function to fetch notifications
    const fetchNotifications = useCallback(async () => {
        try {
            const response = await axios.get('https://localhost:3001/payment/status', {
                headers: {
                    Authorization: `Bearer ${auth.token}`,
                },
                params: {
                    username: auth.username,
                },
            });

            // Check if there are new notifications
            if (response.data.length > lastNotificationCount) {
                // Only play sound if notifications are not marked as read
                const newNotifications = response.data.filter(notification => !notifications.some(n => n._id === notification._id && n.read)); // Assuming you have a read property
                if (newNotifications.length > 0) {
                    playNotificationSound(); // Play sound when new notifications are fetched
                }
                setNotifications(response.data); // Update notifications state
                setLastNotificationCount(response.data.length); // Update last notification count
            } else if (response.data.length === 0) {
                setNotifications([]); // Clear notifications if none are present
                setLastNotificationCount(0); // Reset last notification count
            }
        } catch (error) {
            console.error("Error fetching notifications:", error);
        }
    }, [auth.token, auth.username, lastNotificationCount, notifications]);

    useEffect(() => {
        if (!auth.token) {
            navigate('/'); // Redirect to home if not logged in
        } else {
            // Fetch notifications every 3 seconds
            const intervalId = setInterval(fetchNotifications, 3000);
            return () => clearInterval(intervalId); // Cleanup on unmount
        }
    }, [auth.token, navigate, fetchNotifications]);

    if (!auth.token) {
        return (
            <div className="dashboard-container">
                <h1>Welcome to <span className="highlight">TransactIO</span></h1>
                <p>Please log in to access your account and make payments.</p>
            </div>
        );
    }

    const banks = ["ABSA", "NEDBANK", "FNB", "CAPITEC", "STANDARD BANK"];
    const randomBank = banks[Math.floor(Math.random() * banks.length)];

    const maskAccountNumber = (accountNumber) => {
        return accountNumber.slice(0, -3).replace(/./g, 'x') + accountNumber.slice(-3);
    };

    const bankingDetails = {
        accountNumber: maskAccountNumber(auth.accountNumber || "123456789"), // Use the account number from auth
        bankName: randomBank,
        balance: "R100,000"
    };

    return (
        <div className="dashboard-container">
            <h1>Welcome to TransactIO</h1>
            <p>Hello, {auth.username}</p>
            <div className="dashboard">
                <h2>Banking Details</h2>
                <p1>Account Number: {bankingDetails.accountNumber}</p1>
                <p1>Bank Name: {bankingDetails.bankName}</p1>
                <p1>Balance: {bankingDetails.balance}</p1>
                <NavLink to="/paymentCreate">
                    <button>Make International Payment</button>
                </NavLink>
            </div>
            <div className="notifications">
                <h2>Notifications</h2>
                {notifications.length === 0 ? (
                    <p>No new notifications</p>
                ) : (
                    <ul>
                        {notifications.map(notification => (
                            <li key={notification._id}>
                                <p>Recipient: {notification.recipientName}</p>
                                <p>Amount: {notification.amount} {notification.currency}</p>
                                <p>Date: {notification.date}</p>
                                <p>Status: {notification.status}</p>
                            </li>
                        ))}
                    </ul>
                )}
            </div>
        </div>
    );
};

export default Home;
