import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Register.css'; // styling for register

// Register component
export default function Register() {
    // State to manage form data and error messages
    const [form, setForm] = useState({
        firstName: '',
        lastName: '',
        email: '',
        username: '',
        password: '',
        confirmPassword: '',
        accountNumber: '',
        idNumber: ''
    });

    const [errors, setErrors] = useState({}); // State to manage error messages
    const navigate = useNavigate();

    // Function to update form state
    function updateForm(value) {
        return setForm((prev) => {
            return { ...prev, ...value };
        });
    }

    // Function to handle form submission
    async function onSubmit(e) {
        e.preventDefault();

        const newUser = { ...form };

        // Send a POST request to the server
        await fetch('http://localhost:3000/register', { // Use http:// instead of https://
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(newUser)
        })
            .then(response => {
                if (!response.ok) {
                    return response.json().then(data => {
                        // Capture validation errors from the server
                        const fieldErrors = {};
                        data.errors.forEach(error => {
                            fieldErrors[error.param] = error.msg; // Map error messages to fields
                        });
                        setErrors(fieldErrors);
                        throw new Error(`HTTP error! status: ${response.status}`);
                    });
                }
                return response.json();
            })
            .then(data => {
                console.log('Registration successful:', data);
                // Reset the form state
                setForm({
                    firstName: '',
                    lastName: '',
                    email: '',
                    username: '',
                    password: '',
                    confirmPassword: '',
                    accountNumber: '',
                    idNumber: ''
                });
                setErrors({}); // Clear errors on successful registration
                // Navigate to the login page
                navigate('/login');
            })
            .catch(error => {
                console.error('Error during registration:', error);
            });
    }

    // JSX to render the form
    return (
        <div className='register-container'>
            <div className='register-card'>
                <h3>Register</h3>
                <form onSubmit={onSubmit}>
                    <div className="form-group">
                        <label htmlFor="firstName">First Name</label>
                        <input
                            type="text"
                            className="form-control"
                            id='firstName'
                            value={form.firstName}
                            onChange={(e) => updateForm({ firstName: e.target.value })}
                            required
                        />
                        {errors.firstName && <small className="text-danger">{errors.firstName}</small>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="lastName">Last Name</label>
                        <input
                            type="text"
                            className="form-control"
                            id='lastName'
                            value={form.lastName}
                            onChange={(e) => updateForm({ lastName: e.target.value })}
                            required
                        />
                        {errors.lastName && <small className="text-danger">{errors.lastName}</small>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="email">Email</label>
                        <input
                            type="email"
                            className="form-control"
                            id='email'
                            value={form.email}
                            onChange={(e) => updateForm({ email: e.target.value })}
                            required
                        />
                        {errors.email && <small className="text-danger">{errors.email}</small>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="username">Username</label>
                        <input
                            type="text"
                            className="form-control"
                            id='username'
                            value={form.username}
                            onChange={(e) => updateForm({ username: e.target.value })}
                            required
                        />
                        {errors.username && <small className="text-danger">{errors.username}</small>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            className="form-control"
                            id='password'
                            value={form.password}
                            onChange={(e) => updateForm({ password: e.target.value })}
                            required
                        />
                        {errors.password && <small className="text-danger">{errors.password}</small>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="confirmPassword">Confirm Password</label>
                        <input
                            type="password"
                            className="form-control"
                            id='confirmPassword'
                            value={form.confirmPassword}
                            onChange={(e) => updateForm({ confirmPassword: e.target.value })}
                            required
                        />
                        {errors.confirmPassword && <small className="text-danger">{errors.confirmPassword}</small>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="accountNumber">Account Number</label>
                        <input
                            type="text"
                            className="form-control"
                            id='accountNumber'
                            value={form.accountNumber}
                            onChange={(e) => updateForm({ accountNumber: e.target.value })}
                            required
                        />
                        {errors.accountNumber && <small className="text-danger">{errors.accountNumber}</small>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="idNumber">ID Number</label>
                        <input
                            type="text"
                            className="form-control"
                            id='idNumber'
                            value={form.idNumber}
                            onChange={(e) => updateForm({ idNumber: e.target.value })}
                            required
                        />
                        {errors.idNumber && <small className="text-danger">{errors.idNumber}</small>}
                    </div>
                    <div className='form-group'>
                        <input
                            type='submit'
                            value='Register'
                            className='btn btn-primary'
                        />
                    </div>
                </form>
            </div>
        </div>
    );
}
