import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import '../PostList.css';  // Assuming custom CSS file for extra styling

export default function PostList() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const examplePosts = [
      { id: 1, user: "Tanaka Kandima", caption: "Sunset", image: "sunset.jpg" },
      { id: 2, user: "Kamau Muthoni", caption: "Beach", image: "beach.jpg" },
    ];
    setPosts(examplePosts);
  }, []);

  return (
    <div className="container post-container">
      <h3 className="header text-center">APDS Notice Board</h3>
      <table className="table table-hover" style={{ marginTop: 20 }}>
        <thead className="table-dark">
          <tr>
            <th>User</th>
            <th>Caption</th>
            <th>Image</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {posts.map((post) => (
            <tr key={post.id} className="table-row">
              <td>{post.user}</td>
              <td>{post.caption}</td>
              <td>
                <img
                  src={post.image}
                  alt={post.caption}
                  className="post-image"
                />
              </td>
              <td>
                <Link to={`/edit/${post.id}`} className="btn btn-primary btn-sm">
                  Edit
                </Link>
                <button className="btn btn-danger btn-sm ml-2">
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
