// LoginSelection.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function LoginSelection() {
    const navigate = useNavigate();
    const [hoveredButton, setHoveredButton] = useState(null);

    return (
        <div style={styles.container}>
            <h2 style={styles.heading}>Select Login Type</h2>
            <button 
                onClick={() => navigate('/login')} 
                onMouseEnter={() => setHoveredButton('customer')}
                onMouseLeave={() => setHoveredButton(null)}
                style={{
                    ...styles.button,
                    ...styles.buttonPrimary,
                    ...(hoveredButton === 'customer' ? styles.buttonPrimaryHover : {})
                }}
            >
                Customer Login
            </button>
            <button 
                onClick={() => navigate('/emp')} 
                onMouseEnter={() => setHoveredButton('employee')}
                onMouseLeave={() => setHoveredButton(null)}
                style={{
                    ...styles.button,
                    ...styles.buttonSecondary,
                    ...(hoveredButton === 'employee' ? styles.buttonSecondaryHover : {})
                }}
            >
                Employee Login
            </button>
        </div>
    );
}

const styles = {
    container: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        height: '100vh',
        background: 'linear-gradient(135deg, #f8f9fa, #e9ecef)',
        padding: '20px',
        textAlign: 'center'
    },
    heading: {
        marginBottom: '30px',
        fontSize: '2rem',
        color: '#333',
        fontWeight: '600',
        letterSpacing: '0.5px',
    },
    button: {
        padding: '12px 24px',
        margin: '10px',
        border: 'none',
        borderRadius: '8px',
        fontSize: '1.1rem',
        fontWeight: '500',
        cursor: 'pointer',
        width: '220px',
        transition: 'transform 0.2s ease, box-shadow 0.2s ease',
        boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
    },
    buttonPrimary: {
        backgroundColor: '#007bff',
        color: '#fff',
    },
    buttonPrimaryHover: {
        backgroundColor: '#0056b3',
    },
    buttonSecondary: {
        backgroundColor: '#6c757d',
        color: '#fff',
    },
    buttonSecondaryHover: {
        backgroundColor: '#495057',
    },
};
