import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../AuthContext'; // Import the AuthContext

export default function Login() {
    const [form, setForm] = useState({
        name: '',
        password: ''
    });

    const { setAuth } = useContext(AuthContext); // Use the context
    const navigate = useNavigate();

    function updateForm(value) {
        return setForm((prev) => {
            return { ...prev, ...value };
        });
    }

    async function onSubmit(e) {
        e.preventDefault();

        try {
            // Send a POST request to the server using HTTPS
            const response = await fetch('https://localhost:3001/user/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(form)
            });

            // Check if the response is ok (status in the range 200-299)
            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(`HTTP error! status: ${response.status}, message: ${errorData.message}`);
            }

            const data = await response.json();
            console.log('Login successful:', data);

            // Update the context with the token and username
            setAuth({ token: data.token, username: data.name });

            // Navigate to the home page or another page
            navigate('/');
        } catch (error) {
            console.error('Error during login:', error);
            window.alert(`An error occurred: ${error.message}`);
        }
    }

    return (
        <div className="container">
            <h3>Create New Post</h3>
            <form onSubmit={onSubmit}>
                <div className="form-group">
                    <label htmlFor='user'>User</label>
                    <input
                        type="text"
                        className='form-control'
                        id="user"
                        value={form.user}
                        onChange={(e) => updateForm({ user: e.target.value })}
                    />
                </div>
                <div className="form-group">
                    <label htmlFor='content'>Content</label>
                    <input
                        type="text"
                        className='form-control'
                        id="content"
                        value={form.content}
                        onChange={(e) => updateForm({ content: e.target.value })}
                    />
                </div>
                <div className='form-group'>
                    <label htmlFor='image'>Image</label>
                    <input
                        type='file'
                        className='form-control'
                        id='image'
                        accept='image/*'
                        onChange={handleImageChange}
                    />
                </div>
                <div className='form-group'>
                    <input
                        type='submit'
                        value='Create Post'
                        className='btn btn-primary'
                    />
                </div>
            </form>
        </div>
    );
}
