import React, { useContext } from 'react';
import { AuthContext } from '../AuthContext.js';
import { NavLink } from 'react-router-dom';
import { Button, Card, CardContent, Typography, Container, CircularProgress } from '@mui/material';
import { motion } from 'framer-motion';

const Home = () => {
    const { auth } = useContext(AuthContext);

    if (!auth.token) {
        return (
            <Container maxWidth="sm" style={{ textAlign: 'center', marginTop: '50px' }}>
                <motion.div
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ duration: 1 }}
                >
                    <h1>Welcome to the Payment Portal</h1>
                    <p>Please log in to access your account and make payments.</p>
                    <NavLink to="/login">
                        <Button variant="contained" color="primary">
                            Login
                        </Button>
                    </NavLink>
                </motion.div>
            </Container>
        );
    }

    const banks = ["ABSA", "NEDBANK", "FNB", "CAPITEC", "STANDARD BANK"];
    const randomBank = banks[Math.floor(Math.random() * banks.length)];

    const bankingDetails = {
        accountNumber: "123456789",
        bankName: randomBank,
        balance: "R10,000"
    };

    return (
        <Container maxWidth="md" style={{ marginTop: '50px' }}>
            <motion.div
                initial={{ x: '-100vw' }}
                animate={{ x: 0 }}
                transition={{ type: 'spring', stiffness: 120 }}
            >
                <Typography variant="h4" gutterBottom>
                    Welcome to the Payment Portal
                </Typography>
                <Typography variant="h6">Hello, {auth.username}</Typography>
                <Card style={{ marginTop: '20px' }}>
                    <CardContent>
                        <Typography variant="h5" gutterBottom>
                            Banking Details
                        </Typography>
                        <Typography variant="body1">Account Number: {bankingDetails.accountNumber}</Typography>
                        <Typography variant="body1">Bank Name: {bankingDetails.bankName}</Typography>
                        <Typography variant="body1">Balance: {bankingDetails.balance}</Typography>
                        <NavLink to="/paymentCreate" style={{ textDecoration: 'none' }}>
                            <Button variant="contained" color="primary" style={{ marginTop: '20px' }}>
                                Make International Payment
                            </Button>
                        </NavLink>
                    </CardContent>
                </Card>
            </motion.div>
        </Container>
    );
};

export default Home;
