import React, { useContext, useEffect, useState, useCallback } from 'react';
import { AuthContext } from '../AuthContext.js';
import axios from 'axios';
import './NotificationPage.css';

const NotificationPage = () => {
  const { auth } = useContext(AuthContext);
  const [notifications, setNotifications] = useState([]);

  const fetchNotifications = useCallback(async () => {
    try {
      const response = await axios.get('http://localhost:3001/notifications', {
        headers: {
          Authorization: `Bearer ${auth.token}`,
        },
        params: {
          username: auth.username,
        },
      });
      setNotifications(response.data);
    } catch (error) {
      console.error("Error fetching notifications:", error);
    }
  }, [auth.token, auth.username]);

  useEffect(() => {
    if (auth.token) {
      fetchNotifications();
    }
  }, [auth.token, fetchNotifications]);

  const markAsRead = async (id) => {
    try {
      await axios.put(`http://localhost:3001/notifications/${id}/read`, {}, {
        headers: {
          Authorization: `Bearer ${auth.token}`,
        },
      });
      setNotifications(notifications.map(notification =>
        notification._id === id ? { ...notification, read: true } : notification
      ));
    } catch (error) {
      console.error("Error marking notification as read:", error);
    }
  };

  return (
    <div className="notification-page">
      <h1>Notifications</h1>
      <ul>
        {notifications.map(notification => (
          <li key={notification._id} className={notification.read ? 'read' : 'unread'}>
            <p>{notification.message}</p>
            {!notification.read && (
              <button onClick={() => markAsRead(notification._id)}>Mark as Read</button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default NotificationPage;
