import React, { useContext } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Navbar from './components/navbar.js'; // Ensure the case matches the file name
import Register from './components/register.js'; // Ensure the case matches the file name
import Login from './components/login.js'; // Ensure the case matches the file name
import { AuthContext } from '../AuthContext.js'; // Import the AuthProvider and AuthContext
import PaymentPortal from './components/paymentPortal.js'; // Ensure the case matches the file name

import './App.css';

const App = () => {
  const { auth } = useContext(AuthContext); // Use the context

  return (
    <AuthProvider>
      <Router>
        <div className="App">
          <Navbar />
          <Routes>
            <Route exact path="/register" element={<Register />} />
            <Route exact path="/login" element={<Login />} />
            {auth.username ? (
              <Route exact path="/paymentCreate" element={<PaymentPortal />} />
            ) : (
              <Route path="/paymentCreate" element={<Navigate to="/login" />} />
            )}
          </Routes>
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
