import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function ForgotPassword() {
    const [email, setEmail] = useState('');
    const navigate = useNavigate();

    async function onSubmit(e) {
        e.preventDefault();

        try {
            // Send a POST request to the server using HTTPS
            const response = await fetch('https://localhost:3001/user/forgot-password', { // Ensure the URL is correct
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ email })
            });

            // Check if the response is ok (status in the range 200-299)
            if (!response.ok) {
                let errorData;
                try {
                    errorData = await response.json();
                } catch (jsonError) {
                    throw new Error(`HTTP error! status: ${response.status}, message: ${response.statusText}`);
                }
                throw new Error(`HTTP error! status: ${response.status}, message: ${errorData.message}`);
            }

            const data = await response.json();
            console.log('Forgot password request successful:', data);

            // Navigate to the login page or show a success message
            window.alert('Password reset link has been sent to your email');
            navigate('/login');
        } catch (error) {
            console.error('Error during forgot password request:', error);
            window.alert(`An error occurred: ${error.message}`);
        }
    }

    return (
        <div className="container">
            <h3>Forgot Password</h3>
            <form onSubmit={onSubmit}>
                <div className="form-group">
                    <label htmlFor='email'>Email</label>
                    <input
                        type="email"
                        className='form-control'
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div className='form-group'>
                    <input
                        type='submit'
                        value='Submit'
                        className='btn btn-primary'
                    />
                </div>
            </form>
        </div>
    );
}
