import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from '../AuthContext.js';
import { NavLink, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Home.css';

const Home = () => {
    const { auth } = useContext(AuthContext);
    const navigate = useNavigate();
    const [approvedPayments, setApprovedPayments] = useState([]);
    const [showNotifications, setShowNotifications] = useState(false);

    useEffect(() => {
        if (!auth.token) {
            navigate('/'); // Redirect to home if not logged in
        }
    }, [auth.token, navigate]);

    const fetchApprovedPayments = async () => {
        try {
            const response = await axios.get('https://localhost:3001/payment/approved', {
                headers: {
                    Authorization: `Bearer ${auth.token}`,
                },
                params: {
                    username: auth.username,
                },
            });
            setApprovedPayments(response.data);
        } catch (error) {
            console.error("Error fetching approved payments:", error);
        }
    };

    const handleNotificationClick = () => {
        setShowNotifications(!showNotifications);
        if (!showNotifications) {
            fetchApprovedPayments();
        }
    };

    if (!auth.token) {
        return (
            <div className="dashboard-container">
                <h1>Welcome to <span className="highlight">TransactIO</span></h1>
                <p>Please log in to access your account and make payments.</p>
            </div>
        );
    }

    const banks = ["ABSA", "NEDBANK", "FNB", "CAPITEC", "STANDARD BANK"];
    const randomBank = banks[Math.floor(Math.random() * banks.length)];

    const maskAccountNumber = (accountNumber) => {
        return accountNumber.slice(0, -3).replace(/./g, 'x') + accountNumber.slice(-3);
    };

    const bankingDetails = {
        accountNumber: maskAccountNumber(auth.accountNumber || "123456789"), // Use the account number from auth
        bankName: randomBank,
        balance: "R100,000"
    };

    return (
        <div className="dashboard-container">
            <h1>Welcome to TransactIO</h1>
            <p>Hello, {auth.username}</p>
            <div className="dashboard">
                <h2>Banking Details</h2>
                <p1>Account Number: {bankingDetails.accountNumber}</p1>
                <p1>Bank Name: {bankingDetails.bankName}</p1>
                <p1>Balance: {bankingDetails.balance}</p1>
                <NavLink to="/paymentCreate">
                    <button>Make International Payment</button>
                </NavLink>
                <div className="alert alert-info">
                    Note: All payments will be processed once approved by an employee.
                </div>
                <div className="notification-icon" onClick={handleNotificationClick}>
                    <img src="notification-icon.png" alt="Notifications" />
                    {approvedPayments.length > 0 && <span className="notification-count">{approvedPayments.length}</span>}
                </div>
                {showNotifications && (
                    <div className="notifications">
                        <h3>Approved Transactions</h3>
                        <ul>
                            {approvedPayments.map(payment => (
                                <li key={payment._id}>
                                    <p>Recipient: {payment.recipientName}</p>
                                    <p>Amount: {payment.amount} {payment.currency}</p>
                                    <p>Date: {payment.date}</p>
                                    <p>Status: {payment.status}</p>
                                </li>
                            ))}
                        </ul>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Home;
