import React, { useContext, useEffect, useState, useCallback } from 'react';
import { AuthContext } from '../AuthContext.js';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function EmpHome() {
    const { auth } = useContext(AuthContext);
    const navigate = useNavigate();
    const [pendingPayments, setPendingPayments] = useState([]);

    const fetchPendingPayments = useCallback(async () => {
        try {
            const response = await axios.get('https://localhost:3001/payment/pending', {
                headers: {
                    Authorization: `Bearer ${auth.token}`,
                },
            });
            setPendingPayments(response.data);
        } catch (error) {
            console.error("Error fetching pending payments:", error);
        }
    }, [auth.token]);

    useEffect(() => {
        if (!auth.token) {
            navigate('/'); // Redirect to home if not logged in
        } else {
            fetchPendingPayments();
        }
    }, [auth.token, navigate, fetchPendingPayments]);

    const updatePaymentStatus = async (id, status) => {
        try {
            // Update the payment status
            await axios.put(`https://localhost:3001/payment/${id}/status`, { status }, {
                headers: {
                    Authorization: `Bearer ${auth.token}`,
                },
            });

            // Log the transaction history
            const payment = pendingPayments.find(payment => payment._id === id);
            if (payment) {
                await axios.post('https://localhost:3001/payment/history', {
                    recipientName: payment.recipientName,
                    amount: payment.amount,
                    currency: payment.currency,
                    status: status,
                    date: new Date().toISOString(),
                }, {
                    headers: {
                        Authorization: `Bearer ${auth.token}`,
                    },
                });
            }

            fetchPendingPayments(); // Refresh the list after updating
        } catch (error) {
            console.error("Error updating payment status:", error);
        }
    };

    return (
        <div style={styles.container}>
            <h1 style={styles.greeting}>Hello, {auth.username}!</h1>
            <p style={styles.date}>Today is {new Date().toLocaleDateString()}</p>
            <h2 style={styles.heading}>Pending Payments</h2>
            <div style={styles.list}>
                {pendingPayments.map(payment => (
                    <div key={payment._id} style={styles.card}>
                        <p style={styles.text}><strong>Recipient:</strong> {payment.recipientName}</p>
                        <p style={styles.text}><strong>Amount:</strong> {payment.amount} {payment.currency}</p>
                        <p style={styles.text}><strong>Date:</strong> {new Date(payment.date).toLocaleDateString()}</p>
                        <div style={styles.buttonContainer}>
                            <button style={styles.approveButton} onClick={() => updatePaymentStatus(payment._id, 'approved')}>Approve</button>
                            <button style={styles.disapproveButton} onClick={() => updatePaymentStatus(payment._id, 'disapproved')}>Disapprove</button>
                        </div>
                    </div>
                ))}
            </div>
            <button style={styles.historyButton} onClick={() => navigate('/transaction-history')}>View Transaction History</button>
        </div>
    );
}

const styles = {
    container: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        padding: '20px',
        backgroundColor: '#f8f9fa',
        minHeight: '100vh',
        marginTop: '100px',
        fontFamily: 'Arial, sans-serif',
    },
    greeting: {
        fontSize: '1.8rem',
        color: '#000',
        marginBottom: '8px',
    },
    date: {
        fontSize: '1rem',
        color: '#000',
        marginBottom: '20px',
    },
    heading: {
        fontSize: '1.5rem',
        color: '#000',
        marginBottom: '20px',
    },
    list: {
        width: '100%',
        maxWidth: '800px',
        display: 'grid',
        gap: '20px',
    },
    card: {
        backgroundColor: '#fff',
        padding: '16px',
        borderRadius: '8px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
        transition: 'transform 0.2s ease-in-out',
    },
    text: {
        color: '#000', // Ensures all paragraph text is black
    },
    buttonContainer: {
        display: 'flex',
        gap: '10px',
        marginTop: '10px',
    },
    approveButton: {
        padding: '8px 16px',
        backgroundColor: '#4CAF50',
        color: '#fff',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        transition: 'background-color 0.2s',
    },
    disapproveButton: {
        padding: '8px 16px',
        backgroundColor: '#f44336',
        color: '#fff',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        transition: 'background-color 0.2s',
    },
    historyButton: {
        marginTop: '30px',
        padding: '10px 20px',
        backgroundColor: '#1976D2',
        color: '#fff',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        fontSize: '1rem',
    },
};
