import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import '../App.css';

const Post = (props) => (
  <tr>
    <td>{props.post.user}</td>
    <td>{props.post.content}</td>
    <td>
      {props.post.image && (
        <img
          src={`data:image/jpeg;base64,${props.post.image}`} // Convert base64 string to image
          alt="Post Image"
          style={{ maxWidth: '100px', maxHeight: '100px', objectFit: 'cover' }} // Ensure the image fits within the size limits
        />
      )}
    </td>
    <td>
      <button
        className="btn btn-link"
        onClick={() => {
          props.deletePost(props.post._id);
        }}
      >
        Delete
      </button>
    </td>
  </tr>
);

const PostList = ({ postId }) => {
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);

  const getPosts = async (id) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`https://localhost:3000/posts/`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setPosts([data]); // Wrap the single post in an array
    } catch (error) {
      setError(error.message);
    }
  };

  useEffect(() => {
    if (postId) {
      getPosts(postId);
    }
  }, [postId]);

  const deletePost = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await fetch(`https://localhost:3000/api/posts/${id}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      setPosts(posts.filter((post) => post._id !== id));
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div>
      <h3>Post List</h3>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <table className="table table-striped" style={{ marginTop: 20 }}>
        <thead>
          <tr>
            <th>User</th>
            <th>Content</th>
            <th>Image</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {posts.map((post) => (
            <Post post={post} deletePost={deletePost} key={post._id} />
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PostList;
