// LoginSelection.js
import React from 'react';
import { useNavigate } from 'react-router-dom';

export default function LoginSelection() {
    const navigate = useNavigate();

    const handleCustomerLogin = () => {
        navigate('/login'); // Redirect to Customer Login
    };

    const handleEmployeeLogin = () => {
        navigate('/emp'); // Redirect to Employee Login
    };

    return (
        <div className="login-selection-container">
            <h2>Please select your login type:</h2>
            <button onClick={handleCustomerLogin} className="btn btn-primary">Customer Login</button>
            <button onClick={handleEmployeeLogin} className="btn btn-secondary">Employee Login</button>
        </div>
    );
}
