import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import '../App.css';

const Post = (props) => (
  <tr>
    <td>{props.post.user}</td>
    <td>{props.post.content}</td>
    <td>
      {props.post.image && (
        <img
          // Convert base64 string to image
          src={`data:image/jpeg;base64, ${props.post.image}`}
          alt="Post Image"
          style={{ maxWidth: '100px', maxHeight: '100px', objectFit: 'cover' }}
        />
      )}
    </td>
    <td>
      <button className='btn btn-link' onClick={() => {
        props.deletePost(props.post._id);
      }}>
        Delete
      </button>
    </td>
  </tr>
);

export default function PostList() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    async function getPosts() {
      try {

        const response = await fetch('http://localhost:3001/post/');

        // Log the response to see what is being returned
        console.log(response);

        if (!response.ok) {
          const message = `An error occurred: ${response.statusText}`;
          window.alert(message);
          return;
        }

        // Log the response text to see the actual content
        const responseText = await response.text();
        console.log(responseText);

        // Parse the response text as JSON
        const posts = JSON.parse(responseText);
        setPosts(posts);
      } catch (error) {
        
        console.error('Fetch error:', error); // Log the error details
        window.alert(`An error occurred: ${error.message}`);
      }
    }
    getPosts();
  }, []);

  async function deletePost(id) {
    const token = localStorage.getItem('jwt');
    await fetch(`http://localhost:3001/post/${id}`, {
      method: 'DELETE',
      headers: {
        'Authorization': `Bearer ${token}`,
      },
    });

    const newPosts = posts.filter((el) => el._id !== id);
    setPosts(newPosts);
  }

  function PostList() {
    return posts.map((post) => {
      return (
        <Post post={post} deletePost={() => deletePost(post._id)} key={post._id} />
      );
    });
  }

  return (
    <div className='container'>
      <h3 className='header'>APDS Notice Board</h3>
      <table className='table table-striped' style={{ marginTop: 20 }}>
        <thead>
          <tr>
            <th>User</th>
            <th>Caption</th>
            <th>Image</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {posts.map((post) => (
            <tr key={post._id}>
              <td>{post.user}</td>
              <td>{post.content}</td>
              <td><img src={post.image} alt={post.content} style={{ width: '100px' }} /></td>
              <td>
                <Link to={`/edit/${post._id}`} className='btn btn-primary'>Edit</Link>
                <button className='btn btn-danger' style={{ marginLeft: '10px' }} onClick={() => deletePost(post._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
