import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../AuthContext.js";
import axios from "axios";
import "./PaymentPortal.css";

const currencySymbols = {
  ZAR: "R",
  USD: "$",
  GBP: "£",
  INR: "₹",
  JPY: "¥",
};

const PaymentPortal = () => {
  const { auth, logout } = useContext(AuthContext); 
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    recipientName: "",
    recipientBank: "",
    recipientAccountNo: "",
    amount: "",
    swiftCode: "",
    username: auth.username,
    date: new Date().toISOString().split("T")[0],
    currency: "ZAR",
  });
  const [successMessage, setSuccessMessage] = useState("");
  //Displaying the error message
  const [errorMessage, setErrorMessage] = useState(""); 
  // Set the duration of user inactivity before auto-logout [3 minutes]
  const timeoutDuration = 180000; 
  let timeoutId;

  // Handle user inactivity timeout
  const resetTimeout = () => {
    // Clearing the existing timeout
    clearTimeout(timeoutId); 
    timeoutId = setTimeout(() => {
      handleLogout();
      // Reseting the timeout
    }, timeoutDuration); 
  };

  const handleLogout = () => {
    logout(); // Log the user out
    navigate("/login"); // Redirect to login page
    alert("You have been logged out due to inactivity.");
  };

  // Set up event listeners for user activity
  useEffect(() => {
    const events = ["mousemove", "keydown", "scroll", "click"];

    // Start the timer when the component mounts
    resetTimeout();

    // Reset the timer on any user interaction
    events.forEach((event) => {
      window.addEventListener(event, resetTimeout);
    });

    // Clean up the event listeners when the component unmounts
    return () => {
      clearTimeout(timeoutId); // Clear the timeout on unmount
      events.forEach((event) => {
        window.removeEventListener(event, resetTimeout);
      });
    };
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleCurrencyChange = (e) => {
    setFormData({ ...formData, currency: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(
        "https://localhost:3001/payment",
        {
          ...formData,
          currencySymbol: currencySymbols[formData.currency],
        },
        {
          headers: {
            Authorization: `Bearer ${auth.token}`,
          },
        }
      );
      setSuccessMessage("Payment has been made successfully!");
       // Clear any previous error message
      setErrorMessage("");
      setFormData({
        recipientName: "",
        recipientBank: "",
        recipientAccountNo: "",
        amount: "",
        swiftCode: "",
        username: auth.username,
        date: new Date().toISOString().split("T")[0],
        currency: "ZAR",
      });
    } catch (error) {
      console.error("Error making payment:", error);
      setSuccessMessage("");
      setErrorMessage(error.response?.data?.error || "Failed to make payment");
    }
  };

  return (
    <div className="payment-container">
      <div className="payment-card">
        <h3>Payment Portal</h3>
        <form onSubmit={handleSubmit} className="payment-form">
          <div className="form-group">
            <label>Recipient's Name:</label>
            <input
              type="text"
              name="recipientName"
              value={formData.recipientName}
              onChange={handleChange}
              placeholder="Recipient's Name"
              required
            />
          </div>
          <div className="form-group">
            <label>Recipient's Bank:</label>
            <input
              type="text"
              name="recipientBank"
              value={formData.recipientBank}
              onChange={handleChange}
              placeholder="Recipient's Bank"
              required
            />
          </div>
          <div className="form-group">
            <label>Recipient's Account No:</label>
            <input
              type="text"
              name="recipientAccountNo"
              value={formData.recipientAccountNo}
              onChange={handleChange}
              placeholder="Recipient's Account No"
              required
            />
          </div>
          <div className="form-group">
            <label>Currency:</label>
            <select
              name="currency"
              value={formData.currency}
              onChange={handleCurrencyChange}
              required
            >
              <option value="ZAR">ZAR - South African Rand</option>
              <option value="USD">USD - Dollar</option>
              <option value="GBP">GBP - Pound</option>
              <option value="INR">INR - Rupees</option>
              <option value="JPY">JPY - Yen</option>
              {/* Add more currencies as needed */}
            </select>
          </div>
          <div className="form-group">
            <label>Amount to Transfer:</label>
            <input
              type="number"
              name="amount"
              value={formData.amount}
              onChange={handleChange}
              placeholder={`Amount (${currencySymbols[formData.currency]})`}
              required
            />
          </div>
          <div className="form-group">
            <label>SWIFT Code:</label>
            <input
              type="text"
              name="swiftCode"
              value={formData.swiftCode}
              onChange={handleChange}
              placeholder="SWIFT Code"
              required
            />
          </div>
          <input type="hidden" name="username" value={formData.username} />
          <input type="hidden" name="date" value={formData.date} />
          <button type="submit">Pay Now</button>
          <button
            type="button"
            onClick={() =>
              setFormData({
                recipientName: "",
                recipientBank: "",
                recipientAccountNo: "",
                amount: "",
                swiftCode: "",
                username: auth.username,
                date: new Date().toISOString().split("T")[0],
                currency: "ZAR",
              })
            }
          >
            Cancel
          </button>
        </form>
        {successMessage && (
          <div className="alert alert-success">{successMessage}</div>
        )}
        {errorMessage && (
          <div className="alert alert-danger">{errorMessage}</div>
        )}
      </div>
    </div>
  );
};

export default PaymentPortal;
