import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import '../App.css';

const Post = (props) => (
  <tr>
    <td>{props.post.user}</td>
    <td>{props.post.content}</td>
    <td>
      {props.post.image && (
        <img
          //Convert base64 string to image
          src={'data:image/jpeg;base64, ${props.post.image}'}
          alt="Post Image"
          style={{ maxWidth: '100px', maxHeight: '100px', objectFit: 'cover' }}
        />
      )}
    </td>
    <td>
      <button className='btn btn-link' onClick={() => {
        props.deletePost(props.post._id);
      }}>
        Delete
      </button>
    </td>
  </tr>
);

export default function PostList() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    async function getPosts() {
      const response = await fetch('https://localhost:3000/post/');

      if (!response.ok) {
        const message = 'An error occured: ${response.statusText}';
        window.alert(message);
        return;
      }

      const posts = await response.json();
      setPosts(posts);
    }
    getPosts();
    return;

  }, [posts.length]);

  async function deletePost(id) {
    const token = localStorage.getItem('jwt');
    await fetch('https://localhost:3000/post/${id}', {
      method: 'DELETE',
      headers: {
        'Authorization': 'Bearer ${token}',
      },
    });

    const newPosts = posts.filter((el) => el.id !== id);
    setPosts(newPosts);
  }

  function PostList() {
    return posts.map((post) => {
      return(
        <Post post={post} deletePost={() => deletePost(post.id)} key={post.id} />
      );
    });
  }

  return (
    <div className='container'>
      <h3 className='header'>APDS Notice Board</h3>
      <table className='table table-striped' style={{ marginTop: 20 }}>
        <thead>
          <tr>
            <th>User</th>
            <th>Caption</th>
            <th>Image</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {post.map((post) => (
            <tr key={post.id}>
              <td>{post.user}</td>
              <td>{post.caption}</td>
              <td><img src={post.image} alt={post.caption} style={{ width: '100px' }} /></td>
              <td>
                <Link to={'/edit/${post.id}'} className='btn btn-primary'>Edit</Link>
                <button className='btn btn-danger' style={{ marginLeft: '10px' }}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
