import React, { useContext } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Navbar from './components/navbar.js'; // Ensure the case matches the file name
import PostList from './components/postList.js';
import EditPost from './components/postEdit.js'; // Ensure the case matches the file name
import Register from './components/register.js'; // Ensure the case matches the file name
import Login from './components/login'; // Ensure the case matches the file name
import { AuthProvider, AuthContext } from './AuthContext'; // Import the AuthProvider

import './App.css';

const PrivateRoute = ({ element, ...rest }) => {
    const { auth } = useContext(AuthContext);
    return auth.token ? element : <Navigate to="/login" />;
};

const App = () => {
    return (
        <AuthProvider>
            <Router>
                <div className="App">
                    <Navbar />
                    <Routes>
                        <Route exact path="/" element={<PrivateRoute element={<PostList />} />} />
                        <Route exact path="/postEdit/:id" element={<PrivateRoute element={<EditPost />} />} />
                        <Route exact path="/create" element={<PrivateRoute element={<CreatePost />} />} />
                        <Route exact path="/register" element={<Register />} />
                        <Route exact path="/login" element={<Login />} />
                    </Routes>
                </div>
            </Router>
        </AuthProvider>
    );
}

export default App;
