import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

export default function PostList() {
  const [post, setPosts] = useState([]);


  useEffect(() => {
    const examplePosts = [
      { id: 1, user: "Tanaka Kandima", caption: "Sunset", image: "sunset.jpg" },
      { id: 2, user: "Kamau Muthoni", caption: "Beach", image: "beach.jpg" },
    ];
    setPosts(examplePosts);
  }, []);

  return (
    <div className='container'>
      <h3 className='header'>APDS Notice Board</h3>
      <table className='table table-striped' style={{ marginTop: 20 }}>
        <thead>
          <tr>
            <th>User</th>
            <th>Caption</th>
            <th>Image</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {post.map((post) => (
            <tr key={post.id}>
              <td>{post.user}</td>
              <td>{post.caption}</td>
              <td><img src={post.image} alt={post.caption} style={{ width: '100px' }} /></td>
              <td>
                <Link to={'/edit/${post.id}'} className='btn btn-primary'>Edit</Link>
                <button className='btn btn-danger' style={{ marginLeft: '10px' }}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

}
