import React, { useContext } from 'react';
import { AuthContext } from '../AuthContext.js';
import { NavLink } from 'react-router-dom';
const Home = () => {
    const { auth } = useContext(AuthContext);

    if (!auth.token) {
        return (
            <div className="container">
                <h1>Welcome to TransactIO</h1>
                
            </div>
        );
    }

    return (
        <div className="home-container">
            <div className="left-half">
                <h1>Welcome to TransactIO</h1>
                <p>Hello, {username}</p>
                <div className="banking-details">
                    <h2>Banking Details</h2>
                    <p>Account Number: {bankingDetails.accountNumber}</p>
                    <p>Bank Name: {bankingDetails.bankName}</p>
                    <p>Balance: {bankingDetails.balance}</p>
                </div>
                <div className="payment-receipts">
                    <h2>Payment Receipts</h2>
                    <ul>
                        {paymentReceipts.map(receipt => (
                            <li key={receipt.id}>
                                {receipt.date} - {receipt.amount} ({receipt.type})
                            </li>
                        ))}
                    </ul>
                </div>
            </div>

            <div className="right-half">
                <button className="register-btn">Register</button>
                <button className="login-btn">Login</button>
                <button className="create-payment-btn">Create Payment</button>
            </div>
        </div>
    );
};
   

export default Home;
