import React, { useContext, useEffect, useState, useCallback } from 'react';
import { AuthContext } from '../AuthContext.js';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function TransactionHistory() {
    const { auth } = useContext(AuthContext);
    const navigate = useNavigate();
    const [transactions, setTransactions] = useState([]);

    const fetchTransactions = useCallback(async () => {
        try {
            const response = await axios.get('https://localhost:3001/payment/history', {
                headers: {
                    Authorization: `Bearer ${auth.token}`,
                },
            });
            setTransactions(response.data);
        } catch (error) {
            console.error("Error fetching transaction history:", error);
        }
    }, [auth.token]);

    useEffect(() => {
        if (!auth.token) {
            navigate('/'); // Redirect to home page if not authenticated
        } else {
            fetchTransactions();
        }
    }, [auth.token, fetchTransactions, navigate]);

    return (
        <div style={styles.container}>
            <h2 style={{ color: 'black' }}>Transaction History</h2>
            <ul>
                {transactions.map(transaction => (
                    <li key={transaction._id} style={{ color: 'black' }}>
                        <p>Recipient: {transaction.recipientName}</p>
                        <p>Amount: {transaction.amount} {transaction.currency}</p>
                        <p>Date: {transaction.date}</p>
                        <p>Status: {transaction.status}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
}

const styles = {
    container: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        height: '100vh',
        backgroundColor: '#f8f9fa'
    }
};
