import db from ".../db/conn.mjs"; // Import the database connection module

//Function to handle payment creation
export const createPayment = async (paymentData) => {
    const { recipientName, recipientBank, recipientAccountNumber, amount, swiftCode } = paymentData;

    //Create a new payment document
    const newPayment = {
        recipientName,
        recipientBank,
        recipientAccountNumber,
        amount,
        swiftCode,
        loginID,
        createdAt: new Date()
    };

    //Get the payments collection from the database
    const collection = await db.collection("payments");

    //Insert the new payment document into the collection
    const result = await collection.insertOne(newPayment);

    return result;
};
